mapper.select:
SELECT MUSIC_NUMBER, MUSIC_TITLE, MUSIC_SINGER, MUSIC_LYRICS, MUSIC_REGISTER_DATE, MUSIC_UPDATE_DATE
FROM TBL_MUSIC
WHERE MUSIC_NUMBER=#{musicNumber}

mapper.update:
UPDATE TBL_MUSIC
SET MUSIC_TITLE=#{musicTitle}, MUSIC_SINGER=#{musicSinger}, MUSIC_LYRICS=#{musicLyrics},  MUSIC_UPDATE_DATE=CURRENT_DATE
WHERE MUSIC_NUMBER=#{musicNumber}
